/**
 * Icon - Componente de iconos de Figma (24x24px, stroke-based)
 *
 * Iconos del sistema de diseño de Figma con 870 iconos disponibles en 20 categorías.
 * Este componente incluye los 31 iconos más comunes pre-cargados.
 *
 * Categorías disponibles:
 * - arrows (70 iconos)
 * - general (126 iconos)
 * - finance (64 iconos)
 * - media (68 iconos)
 * - alerts (17 iconos)
 * - security (28 iconos)
 * - Y 14 categorías más...
 */
export function Icon({
  name,
  size = 24,
  color = 'currentColor',
  strokeWidth = 2,
  className = '',
  style,
  onClick,
}: IconProps) {
  const path = iconPaths[name];

  if (!path) {
    console.warn(`Icon "${name}" not found`);
    return null;
  }

  const classNames = [styles.icon, className].filter(Boolean).join(' ');

  return (
    <svg
      className={classNames}
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      style={style}
      onClick={onClick}
    >
      <path
        d={path}
        stroke={color}
        strokeWidth={strokeWidth}
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}
